DROP TRIGGER IF EXISTS `line_insert_trigger`;
DELIMITER ;;
CREATE TRIGGER `line_insert_trigger` BEFORE INSERT ON `line` FOR EACH ROW BEGIN
    IF NEW.broker_price IS NULL
    THEN
      SET NEW.broker_price = NEW.price +
                             (NEW.price *
                              (SELECT CASE count(bu.owner_commission)
                                      WHEN 0
                                        THEN bu.owner_commission
                                      ELSE (SELECT bu.owner_commission
                                            FROM business bu JOIN broker b ON b.broker_id = bu.user_id
                                            WHERE b.client_id = NEW.user_id OR b.broker_id = NEW.user_id) END
                               FROM business bu JOIN broker b ON b.broker_id = bu.user_id
                               WHERE b.client_id = NEW.user_id)) / 100;
    END IF;
  END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `line_update_trigger`;
DELIMITER ;;
CREATE TRIGGER `line_update_trigger` BEFORE UPDATE ON `line` FOR EACH ROW BEGIN
    SET NEW.broker_price = NEW.price + (NEW.price * (SELECT business.owner_commission
                                                     FROM
                                                       line
                                                       LEFT JOIN broker ON line.user_id = broker.client_id
                                                       LEFT JOIN business ON broker.broker_id = business.user_id
                                                     WHERE
                                                       line.id = NEW.id)) / 100;
  END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `membership_insert`;
DELIMITER ;;
CREATE TRIGGER `membership_insert` BEFORE INSERT ON `membership` FOR EACH ROW BEGIN
  SET NEW.end_date = NOW() + INTERVAL 1 MONTH;
END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `profile_insert_trigger`;
DELIMITER ;;
CREATE TRIGGER `profile_insert_trigger` BEFORE INSERT ON `profile` FOR EACH ROW BEGIN
    IF EXISTS(SELECT NEW.cpn)
    THEN
      SET NEW.cpn = AES_ENCRYPT(NEW.cpn, 'uiyiouoi^&*^&*23');
    END IF;

    IF EXISTS(SELECT NEW.dob)
    THEN
      SET NEW.dob = AES_ENCRYPT(NEW.dob, 'asdf@#$%@#$%23452345');
    END IF;

    IF EXISTS(SELECT NEW.ssn)
    THEN
      SET NEW.ssn = AES_ENCRYPT(NEW.ssn, ';lk;@!#$!@#$!@#$234');
    END IF;
  END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `profile_update_trigger`;
DELIMITER ;;
CREATE TRIGGER `profile_update_trigger` BEFORE UPDATE ON `profile` FOR EACH ROW BEGIN
    IF NEW.cpn <> OLD.cpn
       OR OLD.cpn IS NULL
    THEN
      SET NEW.cpn = AES_ENCRYPT(NEW.cpn, 'uiyiouoi^&*^&*23');
    END IF;

    IF NEW.dob <> OLD.dob
       OR OLD.dob IS NULL
    THEN
      SET NEW.dob = AES_ENCRYPT(NEW.dob, 'asdf@#$%@#$%23452345');
    END IF;

    IF NEW.ssn <> OLD.ssn
       OR OLD.ssn IS NULL
    THEN
      SET NEW.ssn = AES_ENCRYPT(NEW.ssn, ';lk;@!#$!@#$!@#$234');
    END IF;
  END
;;
DELIMITER ;
